mapper.insert:

  SELECT LAST_INSERT_ID()

insert into image (url, post_id, order_no
  )
values (#{url,jdbcType=VARCHAR}, #{postId,jdbcType=INTEGER}, #{orderNo,jdbcType=INTEGER}
  )

mapper.update:
update image
set url = #{url,jdbcType=VARCHAR},
  post_id = #{postId,jdbcType=INTEGER},
  order_no = #{orderNo,jdbcType=INTEGER}
where image_id = #{imageId,jdbcType=INTEGER}

mapper.select:
select image_id, url, post_id, order_no
from image
where image_id = #{imageId,jdbcType=INTEGER}
